import React, { useCallback } from "react";
import { withRouter } from "react-router";
import app from './base';

import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import { Container, Row, Col, Form, ButtonToolbar, ToggleButtonGroup, ToggleButton, Button } from 'react-bootstrap';
import 'react-bootstrap';

const Interests = ({ history }) => {
    const handleSignUp = useCallback(async event => {
        event.preventDefault();
        const { email, password } = event.target.elements;
        try {
            await app
                .auth()
                .createUserWithEmailAndPassword(email.value, password.value);
            history.push("/interests");
        } catch (error) {
            alert(error);
        }
    }, [history]);

    return (
        <div className="App-main">
            <Container>
                <Row>
                    <Col md={{ span: 4, offset: 4 }}>
                        <h1>Interests</h1>
                        <Form onSubmit={handleSignUp}>
                            <ButtonToolbar>
                                <ToggleButtonGroup type="checkbox" defaultValue={[]}>
                                    <ToggleButton value={1}>Checkbox 1</ToggleButton>
                                    <ToggleButton value={2}>Checkbox 2</ToggleButton>
                                    <ToggleButton value={3}>Checkbox 3</ToggleButton>
                                </ToggleButtonGroup>
                            </ButtonToolbar>
                            <Button type="submit">Sign Up</Button>
                        </Form>
                    </Col>
                </Row>
            </Container>
        </div>
    );
};

export default withRouter(Interests);
